import styled from "@emotion/styled";
import { Grid, Paper, Typography } from "@mui/material";
import { Box } from "@mui/system";
import React, { useState } from "react";

const ImageBox = styled(Paper)({
    width: '80%' , 
    height: '300px' , 
    display:'flex' , 
    justifyContent:'center' , 
    // border:"1px solid black" ,
    margin:'50px auto'
})

const Item = styled(Box)({

})


function Home(){

    const [items , setItems] = useState([{
        name : '상품1',
        price : '13000원',
    },
    {
        name : '상품2',
        price : '23000원',
    },
    {
        name : '상품3',
        price : '33000원',
    },
    {
        name : '상품4',
        price : '43000원',
    },
    {
        name : '상품5',
        price : '53000원',
    },
    {
        name : '상품6',
        price : '63000원',
    },
    {
        name : '상품7',
        price : '63000원',
    },
]);

    return(
        <Box>
            <ImageBox elevation={10}>
                {/* <Box component="img" src="img/test.png" sx={{ width: "80%"}}></Box> */}
            </ImageBox>
        <Grid container spacing={5} sx={{ paddingX:10}}>
            {items.map( (item , index) => (
                <Grid item key={index} md={3} xs={6} >
                <Paper elevation={5} sx={{width:'100%' , height:'400px' , display:'flex' , flexDirection:'column' ,alignItems:'center'}}>
                    <Box component="img" src="" sx={{width:"300px" , height:"300px"}}></Box>  
                    <Typography variant="caption" color="gray" >{item.name}</Typography>
                    <Typography variant="body2" >{item.price}</Typography>
                </Paper>
            </Grid>
            ))}

        </Grid>
        </Box>
    );
}

export default Home;
