import { Typography } from "@mui/material";
import { Box } from "@mui/system";
import React from "react";
import { useLocation } from "react-router-dom";


function Detail(){

    const location = useLocation();

    const clothes = {
        name : location.state?.name,
        price : location.state?.price,
        image : location.state?.image,
        content : location.state?.content,
        from : location.state?.from
    }

    return(
        <Box>
            <Box sx={{
                display: "grid",
                gap: "3rem",
                width:"90%",
                margin: "auto",
                gridTemplateAreas: `"infoPart imagePart orderPart"`,
                gridTemplateColumns: "320px 1fr 320px"                 
            }}>
                    <Box sx={{ gridArea : 'infoPart'  ,display:'flex' , flexDirection:'column', justifyContent:' space-around' , height:'70%' , marginY:'auto'}}>
                        <Box>
                            <Typography variant="h5">{clothes.name}</Typography>
                            <Typography variant="h5" sx={{fontWeight:'bold' , my: 1}}>{clothes.price}</Typography>
                        </Box>

                        <Box sx={{borderTop: 1 , borderBottom: 1 , py: 4 , mx : 1 , borderColor : 'lightGray'}}>
                            <Typography variant="body2">{clothes.content}</Typography>
                        </Box>
                        <Box>
                            <Typography>원산지 {clothes.from}</Typography>
                            <Typography>배송정보 택배 (해외배송)</Typography>
                            <Typography>배송비 10000원 (100,000원 이상 무료배송)</Typography>
                        </Box>
                    </Box>
                    <Box sx={{ gridArea: 'imagePart' , border:1, height:"500px"}}><Box component="img" src={clothes.image} width="100%" height="100%"></Box></Box>
                    <Box sx={{ gridArea: 'orderPart', border:1 }}> Order</Box>
            </Box>

        </Box>
    );
}

export default Detail;
