import { Label } from "@mui/icons-material";
import { Button, Input, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";
import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import styled from "@emotion/styled";

const StyledTextField = styled(TextField)({
    ' .MuiOutlinedInput-root': {

        '& fieldset': {
            margin:'auto 0',
            borderColor: 'lightGray',
            height: '40px',
          },
          '&:hover fieldset': {
          },
          '&.Mui-focused fieldset': {
            borderColor: 'gray',
          },
    },

})

const InfoPartBox = styled(Box)({
    gridArea : 'infoPart'  ,
    display:'flex' , 
    flexDirection:'column', 
    justifyContent:' space-around' ,
    height:'80%'
})

const InfoSecondBox = styled(Box)({
    borderTop: "1px solid" , 
    borderBottom: "1px solid" , 
    py: 4,
    borderColor : 'lightGray'
})

const InfoThirdBox = styled(Box)({
    display:'flex' , 
    flexDirection:'column'
})

const ImagePartBox = styled(Box)({
    gridArea: 'imagePart', 
    border:0, 
    height:"500px"
})
const OrderPartBox = styled(Box)({
    gridArea: 'orderPart'
})
const OrderFirstBox = styled(Box)({
    display : 'flex' ,
    justifyContent:'space-between' , 
    alignItems:'center'
})
const OrderSecondBox = styled(Box)({
    display : 'flex' , 
    justifyContent:'space-between' , 
    alignItems:'center'
})

const OrderThirdBox = styled(Box)({
    display:'flex' , 
    justifyContent:'right'
})
const OrderFourthBox = styled(Box)({
    display:"flex" , 
    alignItems:'center' , 
    justifyContent:'space-between'
})
const OrderFifthBox = styled(Box)({
    display:"flex" , 
    alignItems:'center' , 
    justifyContent:'space-between'
})
const OrderSixthBox = styled(Box)({
    borderTop:"1px solid" , 
    borderColor:'lightgray' , 
    marginTop:"30px"
})

function Detail(){


    const [totalCost , setTotalCost] = useState(0);

    const shippingFee = 10000;

    const location = useLocation();

    const [num , setNum] = useState(0);
    const onClick = (e) =>{
        if(e.target.id === '-'){
            setNum(prev => prev-1);
        }
        else if(e.target.id === '+'){
            setNum(prev => prev+1);
        }
    }

    const clothes = {
        name : location.state?.name,
        price : location.state?.price,
        image : location.state?.image,
        content : location.state?.content,
        from : location.state?.from
    }

    useEffect( () => {
        if(num <= 0 ) setTotalCost(0);
        else setTotalCost( num * parseInt(clothes.price) + shippingFee);
    } , [num])


    return(
        <Box>
